import React from 'react';
import styles from "./Users.module.css"
import * as axios from 'axios';
import userPhoto from '../../assers/img/user.png';



class Users extends React.Component  {

    componentDidMount() {
        axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response => {

            this.setUsers(response.data.items)

        });
    }


    render () {
        return (
            <div>
                <div>
                    <span className={styles.selectedPage}>1</span>
                    <span>2</span>
                    <span> 3 </span>
                    <span> 4 </span>
                    <span> 5 </span>
                </div>
                {
                    this.props.users.map(user => 
                        <div key={user.id}>
                            <span> 
                                <div>
                                    <img src={user.photos.small != null ? 
                                        user.photos.small : 
                                        userPhoto} className={classes.userPhoto}/>
                                </div>
                                <div>
                                    {user.followed ? 
                                    <button onClick = {() => {this.props.unfollow(user.id)}}> Follow</button> 
                                    : <button onClick = {() => {this.props.follow(user.id)}}>Unfollow</button>}
                                </div>
                            </span>
                            <span>
                                <span>
                                    <div>{user.name}</div>
                                    <div>{user.status}</div>
                                </span>
                                <span>
                                    <div>{"user.location.city"}</div>
                                    <div>{"user.location.country"}</div>
                                </span>
                            </span>
                        </div>
                    )
                }
            </div>
        )

    }
}

export default Users;
