const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS'


let initialState = {
    users:  [
        {id:1, 
            fullName: 'Mike',
            photoUrl: "https://pbs.twimg.com/profile_images/2881220369/2b27ac38b43b17a8c5eacfc443ce3384_400x400.jpeg",
            followed: false,
             status: 'hey', 
             location:{
                    city:'Minsk', 
