import React from 'react';
import { NavLink } from 'react-router-dom';
import classes from "./Dialogs.module.css";
import DialogItem from "./DialogItem"
import DialogMessage from "./DialogMessage";
import { sendMessageCreator, updateNewMessageBodyCreator } from '../redux/dialog-reducer';




const Dialogs = (props) => {
    let state = props.dialogPage;


   // let DialogsElement = state.DialogsData.map( dialog =>  <DialogItem name={dialog.name} id={dialog.id} />)

    let MessangesElement = state.MessageData.map(messange => <DialogMessage messange={messange.messange}/>)

    let newMessageBody = state.newMessageBody;


    let onSendMessageClick = () => {

        props.sendMessage();

    }

    let onNewMessageClick = (e) => {
        let body = e.target.value;
        props.updateNewMessageBody(body);

    }

    return (
        <div className={classes.dialogs}>
            <div className={classes.dialogsItems}>
               // ?
            </div>
            <div className={classes.messanges}>
                <div> {MessangesElement} </div>
                <div>
                    <div> <textarea 
                        value={newMessageBody}
                        onChange={onNewMessageClick}> </textarea> </div>
                    <div> <button onClick={onSendMessageClick}>click</button> </div>
                </div>
            </div>
        </div>
    )
}

export default Dialogs;
