import React from 'react';



class ProfileStatus extends React.Component  {
    statusInputRef = React.createRef()
    state={
        editMode: false,

    }

     activateEditMode () {
         //console.log( this.state.editMode)
         this.setState({
             editMode: true
         })

     }
     deactivateEditMode () {
        //console.log( this.state.editMode)
        this.setState({
            editMode: false
        })
       this.state.updateStatus(this.statusInputRef.current.value);

    }

    render(){
        return(
            <div>
                {!this.state.editMode && 
                    <div>
                        <span onClick={() => {this.activateEditMode.bind(this)}} >{this.props.status}</span>
                    </div>
                }
                {this.state.editMode && 
                    <div>
                        <input 
                        ref={this.statusInputRef} autoFocus={true} onBlur={this.deactivateEditMode.bind(this)} value={this.props.status} />
                    </div>
                }
            </div>
        )  
    }

}

export default ProfileStatus;
