import React from 'react';
import classes from "./Users.module.css"
import * as axios from 'axios';
import userPhoto from '../../assers/img/user.png';



class Users extends React.Component  {

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {

            this.props.setUsers(response.data.items)
            this.props.serTotalUsersCount(response.data.totalCount)
        });
    }

    onPostChanged = (p) => {
        this.props.setCurrentPage(pageNumber)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`).then(response => {

            this.setUsers(response.data.items)

        });
    }


    render () {

        let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize);

        let pages = [];
        for (let i = 1; i <= pagesCount; i++) {
            pages.push(i);

        }

        return (
            <div>
                <div>
                    {pages.map((p) => {
                   return  <span className={
                        this.props.currentPage === p ? classes.selectedPage : ""}
                        onClick={(event) => {this.onPostChanged(p)}}>
                            {p}
                    </span>})}

                </div>
                {
                    this.props.users.map(user => 
                        <div key={user.id}>
                            <span> 
                                <div>
                                    <img src={user.photos.small != null ? 
                                        user.photos.small : 
                                        userPhoto} className={classes.userPhoto}/>
                                </div>
                                <div>
                                    {user.followed ? 
                                    <button onClick = {() => {this.props.unfollow(user.id)}}> Follow</button> 
                                    : <button onClick = {() => {this.props.follow(user.id)}}>Unfollow</button>}
                                </div>
                            </span>
                            <span>
                                <span>
                                    <div>{user.name}</div>
                                    <div>{user.status}</div>
                                </span>
                                <span>
                                    <div>{"user.location.city"}</div>
                                    <div>{"user.location.country"}</div>
                                </span>
                            </span>
                        </div>
                    )
                }
            </div>
        )

    }
}

export default Users;
