import React from 'react';
import classes from "./Users.module.css"

import userPhoto from '../../assers/img/user.png';
import { NavLink } from 'react-router-dom';
import * as axios from 'axios';
import { usersAPI } from '../../api/api';



let Users = (props) => {
    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);

    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i);

    }
    return (
        <div>
            <div>
                {pages.map((p) => {
               return  <span className={
                    props.currentPage === p ? classes.selectedPage : ""}
                    onClick={(event) => {props.onPageChanged(p)}}>
                        {p}
                </span>})}

            </div>
            {
                props.users.map(user => 
                    <div key={user.id}>
                        <span> 
                            <div>
                                <NavLink to={'/profile/' + user.id}>
                                <img src={user.photos.small != null ? 
                                    user.photos.small : 
                                    userPhoto} className={classes.userPhoto}/>
                                </NavLink>
                            </div>
                            <div>
                                {user.followed ? 
                                <button disabled={props.followingInProgress.some(id => id === user.id)} onClick = {() => {
                                        props.toggleFollowingProgress(true, user.id);

                                        usersAPI.follow(user.id).then(response => {
                                            if(response.data.resultCode === 0) {
                                                props.follow(user.id)
                                            }
                                        props.toggleFollowingProgress(false, user.id);
                                        })
//274619bf-c707c44db-90e2-34bfeccdc4fc
                                    }
                                }> Follow</button> 
                                : <button disabled={props.followingInProgress.some(id => id === user.id)} onClick = {() => {
                                    props.toggleFollowingProgress(true, user.id);

                                    usersAPI.un

                                    axios.delete(`https://social-network.samuraijs.com/api/1.0/follow/${user.id}`, {
                                            withCredentials: true,
                                            headers: {"API-KEY": '274619bf-c707c44db-90e2-34bfeccdc4fc'}
                                        } ).then(response => {
                                        if(response.data.resultCode === 0) {
                                            props.unfollow(user.id)
                                        }
                                        props.toggleFollowingProgress(false, user.id);
                                        })

                                    }}>Unfollow</button>}
                            </div>
                        </span>
                        <span>
                            <span>
                                <div>{user.name}</div>
                                <div>{user.status}</div>
                            </span>
                            <span>
                                <div>{"user.location.city"}</div>
                                <div>{"user.location.country"}</div>
                            </span>
                        </span>
                    </div>
                )
            }
        </div>
    )
}



export default Users;
