import React from 'react';
import { NavLink } from 'react-router-dom';
import classes from "./Dialogs.module.css";
import DialogItem from "./DialogItem"
import DialogMessage from "./DialogMessage";
import { sendMessageCreator, updateNewMessageBodyCreator } from '../redux/state';



const Dialogs = (props) => {


    let DialogsElement = props.state.DialogsData.map( dialog =>  <DialogItem name={dialog.name} id={dialog.id} />)

    let MessangesElement = props.state.MessageData.map(messange => <DialogMessage messange={messange.messange}/>)

    let newMessageBody = props.state.newMessageBody;

    let newClick = React.createRef();
    let onSendMessageClick = () => {
        let text = newClick.current.value;
        alert(text);

        props.store.dispath(sendMessageCreator())

    }

    let onNewMessageClick = (e) => {
        let body = e.target.value;
        props.store.dispath(updateNewMessageBodyCreator(body))
    }

    return (
        <div className={classes.dialogs}>
            <div className={classes.dialogsItems}>
                {DialogsElement}
            </div>
            <div className={classes.messanges}>
                <div> {MessangesElement} </div>
                <div>
                    <div> <textarea 
                        value={newMessageBody}
                        onChange={onNewMessageClick}> </textarea> </div>
                    <div> <button onClick={onSendMessageClick}>click</button> </div>
                </div>
            </div>
        </div>
    )
}

export default Dialogs;
