import { authAPI } from "../../api/api";
import {stopSubmit} from 'redux-forms/lib/reducer'

const SET_AUTH_USER_DATA = 'SET_AUTH_USER_DATA';


let initialState = {
   userId: null,
   email: null,
   login: null,
   isAuth: false
}

const authReducer = (state = initialState, action) => {
    switch(action.type){
        case SET_AUTH_USER_DATA:
            return {
                ...state, 
                ...action.payload,

            }

    default:
        return
    }
}

export const setAuthUserData = (userId, email, login, isAuth) => 
    ({type: SET_AUTH_USER_DATA, payload:{userId, email, login, isAuth}});

export const getAuthUserData = () => (dispatch) => {
    authAPI .me().then(response => {
        if(response.data.resultCode === 0){
            let {id, email, login} = response.data.data
          dispatch(setAuthUserData(id, email, login, true));
        }
      });
}

export const login = (email, password, remember) => (dispatch) => {
    let action = 
            dispatch(stopSubmit('login', {_error: 'Common error'}))

    authAPI .login(email, password, remember).then(response => {
        if(response.data.resultCode === 0){
           dispatch(getAuthUserData())
        } else {}

      });
}

export const logout = () => (dispatch) => {
    authAPI .logout().then(response => {
        if(response.data.resultCode === 0){
            dispatch(setAuthUserData(null, null, null, false));
         }
      });
}

export default authReducer;
